import { AnimatedIconProps } from "./types";
import { motion, useAnimate } from "motion/react";

const CandyCaneIcon = ({
  size = 24,
  color = "currentColor",
  strokeWidth = 2,
  className = "",
}: AnimatedIconProps) => {
  const [scope, animate] = useAnimate();

  const hoverAnimation = async () => {
    animate(
      ".cane",
      { rotate: [0, -10, 10, -5, 5, 0] },
      { duration: 0.6, ease: "easeInOut" },
    );
  };

  const resetAnimation = () => {
    animate(".cane", { rotate: 0 }, { duration: 0.2, ease: "easeInOut" });
  };

  return (
    <motion.svg
      ref={scope}
      onHoverStart={hoverAnimation}
      onHoverEnd={resetAnimation}
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      stroke={color}
      strokeWidth={strokeWidth}
      strokeLinecap="round"
      strokeLinejoin="round"
      className={`cursor-pointer ${className}`}
    >
      <motion.g className="cane" style={{ transformOrigin: "12px 12px" }}>
        <path d="M5.7 21a2 2 0 0 1-3.5-2l8.6-14a6 6 0 0 1 10.4 6 2 2 0 1 1-3.464-2 2 2 0 1 0-3.464-2Z" />
        <path d="M17.75 7 15 2.1" />
        <path d="M10.9 4.8 13 9" />
        <path d="m7.9 9.7 2 4.4" />
        <path d="M4.9 14.7 7 18.9" />
      </motion.g>
    </motion.svg>
  );
};

export default CandyCaneIcon;

export interface AnimatedIconProps {
  /** Icon size in pixels or CSS string */
  size?: number | string;
  /** Icon color (defaults to currentColor) */
  color?: string;
  /** SVG stroke width */
  strokeWidth?: number;
  /** Additional CSS classes */
  className?: string;
}
